sys_script_include.script:
var loadData = Class.create();
loadData.prototype = {
    initialize: function(){
        gs.debug('@initialize');
        this.data = d();
    },

	get: function(){
		return this.data;
	},

    load: function(){
        gs.debug('@load')
        this.splitAndSort();
        this.setTranslation('v1'); // converts old header to new field names
        this.processDefinitions();
        this.createQuiz();
    },

    splitAndSort: function(){
        gs.debug('@splitAndSort')
        // splits the data into definition and rounds, and sorts the rounds
        var definitionSheet;
        this.roundSheets = this.data.filter(function(sheet){
            var re = /(\d+)[-_ ]+(.*)/;
            var match = sheet.name.match(re);
            if (match){
                // name matches the pattern of a round. store its number and pass it through the filter
                sheet._sequence = +match[1];
                sheet._name = match[2];
                return true;
            } else {
                // name does not match the pattern of a round; it is the definition sheet and it will not be passed through the filter
                definitionSheet = sheet;
                return false;
            }
        }).sort(function(a, b){
            if (a._sequence < b._sequence){
                return -1;
            } else {
                return 1;
            }
        })    

        //debug
        // this.roundSheets.length = 1;
        //end debug

        this.definitionSheet = definitionSheet;

        return this;
    },

    setTranslation: function(arg){
        gs.debug('@setTranslation')
        switch (arg){
            case 'v1': 
                this.xlate = {
                    'languages.nl.text': 'question_nl',
                    'languages.en.text': 'question_en',
                    'languages.nl.answer': 'answer_nl',
                    'languages.en.answer': 'answer_en',
                    'languages.nl.label': 'label_nl',
                    'languages.en.Label': 'label_en'
                }
                break;
            case 'v2':
                this.xlate = {}
                break;
            default:
                this.xlate = arg;        
        }

        return this.xlate;
    },

    createQuiz: function(){
        gs.debug('@createQuiz')
        var self = this;
        var grQuiz = new GlideRecord('x_0221_pubquiz_quiz');
        grQuiz.setValue('title', self.definitions.title);
        grQuiz.setValue('description', self.definitions.description);
        var quizSydId = grQuiz.insert();

gs.debug('roundSheets length == '+self.roundSheets.length)

        self.roundSheets.forEach(function(roundSheet, index){
            self.createRound(roundSheet, quizSydId, index);
        })
    },

    createRound: function(roundSheet, quizSysId, roundIndex){
        gs.debug('@createRound')
        gs.debug(roundSheet._name)

        var grM2m = new GlideRecord('x_0221_pubquiz_quiz_round_m2m');
        var grRound = new GlideRecord('x_0221_pubquiz_round');
        var roundNumber = 1+roundIndex;

        grRound.setValue('name', roundSheet._name || 'Round '+roundNumber);
        var roundSysId = grRound.insert();

        grM2m.setValue('quiz', quizSysId);
        grM2m.setValue('round', roundSysId);
        grM2m.setValue('round_nr', roundNumber);
        grM2m.insert();

        // after adding the round and linking it to the quiz, add the questions
        // first isolate the rows needed in a new array
        var questions = roundSheet.data.splice(this.definitions.headerRows, this.definitions.QuestionsPerRound);
        var header = roundSheet.data.shift(); // header contains field names and is always the first entry

        var self = this;
        questions.forEach(function(question, index){
            var questionObject = self.createObject(header, question);
            self.createQuestion(questionObject, roundSysId, index);
        })
    },

    createQuestion: function(questionObject, roundSysId, questionIndex){
        gs.debug('@createQuestion')
        var grM2m = new GlideRecord('x_0221_pubquiz_round_question_m2m');
        var grQuestion = new GlideRecord('x_0221_pubquiz_question');

        grQuestion.setValue('question_en', questionObject['languages.en.text'])

        var field;
        for (field in questionObject){
            grQuestion.setValue(field, questionObject[field]);
        }
        var questionSysId = grQuestion.insert();

        grM2m.setValue('round', roundSysId);
        grM2m.setValue('question', questionSysId);
        grM2m.setValue('question_nr', 1+questionIndex);
        grM2m.insert();
    },

    processDefinitions: function(xlate){
        gs.debug('@processDefinitions')
        var defs = {
            columns: []
        };

        this.definitionSheet.data.forEach(function(row, index){
            if (index > 0){ // first row is the header row, we ignore it
                if (row[0] === 'column'){
                    // column names go on to the column array
                    defs.columns.push(row[3])
                } else {
                    // key/value pairs
                    defs[row[0]] = row[1]
                }
            }
        });

        defs.fieldsRow = defs.fieldsRow || 0;

        this.definitions =  defs;

        return defs;
    },

    createObject: function(header, data){
        // creates key/value pairs w/ key from the header, value from same position in data
        gs.debug('@createObject')
        var result = {};
        var xlate = this.xlate;
        gs.debug(JSON.stringify(xlate))

        header.forEach(function(fieldname, index){
            gs.debug(fieldname+' => '+xlate[fieldname]+' = '+data[index])
            // use the translated fieldname if present in xlate, otherwise use the fieldname as
            result[xlate[fieldname] || fieldname] = data[index];
        });

        return result;
    }
}



